import { Component, OnInit } from '@angular/core';
import { EmployeeAPIClient, EmployeeDto } from '../Shared/api-service.service';

@Component({
  selector: 'app-employee',
  templateUrl: './employee.component.html',
  styleUrls: ['./employee.component.css'],
  providers: [EmployeeAPIClient]
})
export class EmployeeComponent implements OnInit {

  constructor(private service: EmployeeAPIClient) { }
  Employee: EmployeeDto[] = [];

  ngOnInit() {
    this.getemployeedetails();
  }

  getemployeedetails()
  {
    this.service.getAllEmployees().subscribe(res => {
      this.Employee = res;
    })
  }










}
<div class="container">
  <h3>Employee</h3>
  <table class="table">
    <thead>
      <tr>
        <td>pkEmpId</td>
        <td>EmpName</td>
        <td>EmpSalary</td>
        <td>EmailId</td>
        <td>password</td>
        <td>fkRelation</td>
        <td>Actions</td>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let ob of Employee">
        <td>{{ob.pkEmpId}}</td>
        <td>{{ob.empName}}</td>
        <td>{{ob.empSalary}}</td>
        <td>{{ob.emailId}}</td>
        <td>{{ob.password}}</td>
        <td>{{ob.fkRelation}}</td>
        <td><i class="fa fa-pencil-square-o" title="edit" [routerLink]="['/edit-employee',ob.pkEmpId]" aria-hidden="true"></i></td>
      </tr>
    </tbody>
  </table>

</div>
