import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import Swal from 'sweetalert2';
import { AddEmployeeDto, EmployeeAPIClient, } from '../Shared/api-service.service';

@Component({
  selector: 'app-add-employee',
  templateUrl: './add-employee.component.html',
  styleUrls: ['./add-employee.component.css'],
  providers: [EmployeeAPIClient]
})
export class AddEmployeeComponent implements OnInit {
  addemp = new AddEmployeeDto
  constructor(private service: EmployeeAPIClient, private routeservice: Router) {

  }
  ngOnInit(): void {
      
    }
  addemployee()
  {

    this.service.addEmployee(this.addemp).subscribe(res => {
      Swal.fire('Employee Added Successfully')
    })
    this.routeservice.navigate(["/employee"])
  }
}
<div class="container">
  <form>
    <div class="form-group">
      <label for="1">EmpName</label>
      <input id="1" style="margin:5px;" class="form-control" type="text" placeholder="Employee Name"[(ngModel)]="addemp.empName" name="EmpName"/>
    </div>
    <div class="form-group">
      <label for="2">EmpSalary</label>
      <input id="2" class="form-control" type="text" placeholder="Employee Salary"[(ngModel)]="addemp.empSalary" name="EmpSalary"/>
    </div>
    <div class="form-group">
      <label for="3">EmpMailId</label>
      <input id="3" class="form-control" type="text" placeholder="Employee MailId"[(ngModel)]="addemp.emailId"name="EmpMailId" />
    </div>
    <div class="form-group">
      <label for="4">EmpPassword</label>
      <input id="4" class="form-control" type="text" placeholder="Employee Password"[(ngModel)]="addemp.password"name="EmpPassword" />
    </div>
    <div class="form-group">
      <label for="5" >fkRelation</label>
      <input id="5" class="form-control" type="text" placeholder="fkRelation"[(ngModel)]="addemp.fkRelation"name="fkRelation" />
    </div>
    <button (click)="addemployee()" style="margin:10px;" type="submit" class="btn btn-primary">Submit</button>
  </form>
</div>
