import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { EmployeeAPIClient, EmployeeDto } from '../Shared/api-service.service';

@Component({
  selector: 'app-edit-employee',
  templateUrl: './edit-employee.component.html',
  styleUrls: ['./edit-employee.component.css'],
  providers: [EmployeeAPIClient]
})
export class EditEmployeeComponent implements OnInit {

  EmpId: any;
  ob = new EmployeeDto;

  constructor(private service: EmployeeAPIClient, private route: ActivatedRoute) { }
  ngOnInit() {
    this.route.params.subscribe(x => {
      this.EmpId = x.id;
      this.getEmployeebyId();
    })

  }



  getEmployeebyId() {
    this.service.getEmployeebyId(this.EmpId).subscribe(res => {
      this.ob = res
    })
  }
}
