import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import Swal from 'sweetalert2';
import { EmployeeAPIClient, EmployeeDto } from '../Shared/api-service.service';

@Component({
  selector: 'app-employee',
  templateUrl: './employee.component.html',
  styleUrls: ['./employee.component.css'],
  providers: [EmployeeAPIClient]
})
export class EmployeeComponent implements OnInit {

  Employee: EmployeeDto[] = [];


  constructor(private service: EmployeeAPIClient, private routeservice: Router) { }

  ngOnInit(): void {
    this.getemployeedetails(event);
    }



  getemployeedetails(event: any): void
  {
    this.service.getAllEmployees().subscribe(res => {
      this.Employee = res;
    })
  }
  deleteEmployee(id:any)
  {
    this.service.deleteEmployee(id).subscribe(res => {
      Swal.fire('Deleted successfully');
      this.getemployeedetails(event);
    })
    

  }









}
<div class="container">
  <h3>Employee</h3>
  <table class="table">
    <thead>
      <tr>
        <td>pkEmpId</td>
        <td>EmpName</td>
        <td>EmpSalary</td>
        <td>EmailId</td>
        <td>password</td>
        <td>fkRelation</td>
        <td>Actions</td>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let ob of Employee">
        <td>{{ob.pkEmpId}}</td>
        <td>{{ob.empName}}</td>
        <td>{{ob.empSalary}}</td>
        <td>{{ob.emailId}}</td>
        <td>{{ob.password}}</td>
        <td>{{ob.fkRelation}}</td>
        <td>
          <i class="fa fa-pencil-square-o" title="edit" [routerLink]="['/edit-employee',ob.pkEmpId]" aria-hidden="true"></i>
          <i class="fa fa-trash" aria-hidden="true" (click)="deleteEmployee(ob.pkEmpId)"></i>
        </td>
      </tr>
    </tbody>
  </table>

  <p-paginator (onPageChange)="getemployeedetails($event)" [first]="1" [rows]="1" [totalRecords]="120" [rowsPerPageOptions]="[10, 20, 30]"></p-paginator>
</div>
