import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import Swal from 'sweetalert2';
import { EmployeeAPIClient, EmployeeDto } from '../Shared/api-service.service';

@Component({
  selector: 'app-edit-employee',
  templateUrl: './edit-employee.component.html',
  styleUrls: ['./edit-employee.component.css'],
  providers: [EmployeeAPIClient]
})
export class EditEmployeeComponent implements OnInit {

  EmpId: any;
  ob! :  EmployeeDto;

  constructor(private service: EmployeeAPIClient, private route: ActivatedRoute) { }
  ngOnInit() {
    this.route.params.subscribe(x => {
      this.EmpId = x.id;
      this.getEmployeebyId();
    })

  }



  getEmployeebyId() {
    debugger;
    this.service.getEmployeebyId(this.EmpId).subscribe(res => {
      this.ob = res
    })
  }
  Updateemployee()
  {
    var x = new EmployeeDto();
    x.empName = this.ob.empName;
    x.empSalary = this.ob.empSalary;
    x.emailId = this.ob.emailId;
    x.password = this.ob.password;
    x.fkRelation = this.ob.fkRelation;
    this.service.updateEmployee(x).subscribe(res => {
      Swal.fire('Employee updated successfully');

    })
  }
}
<div class="container">
  <form>
    <div class="form-group">
      <label for="1">EmpName</label>
      <input id="1" style="margin:5px;" class="form-control" type="text" placeholder="Employee Name" [(ngModel)]="ob.empName" name="EmpName" />
    </div>
    <div class="form-group">
      <label for="2">EmpSalary</label>
      <input id="2" class="form-control" type="text" placeholder="Employee Salary" [(ngModel)]="ob.empSalary" name="EmpSalary" />
    </div>
    <div class="form-group">
      <label for="3">EmpMailId</label>
      <input id="3" class="form-control" type="text" placeholder="Employee MailId" [(ngModel)]="ob.emailId" name="EmpMailId" />
    </div>
    <div class="form-group">
      <label for="4">EmpPassword</label>
      <input id="4" class="form-control" type="text" placeholder="Employee Password" [(ngModel)]="ob.password" name="EmpPassword" />
    </div>
    <div class="form-group">
      <label for="5">fkRelation</label>
      <input id="5" class="form-control" type="text" placeholder="fkRelation" [(ngModel)]="ob.fkRelation" name="fkRelation" />
    </div>
    <button (click)="getEmployeebyId()" style="margin:10px;" type="submit" class="btn btn-primary">Submit</button>
  </form>
</div>
